import{_ as a,c as o,o as d,ag as t}from"./chunks/framework.lo-SgHyK.js";const u=JSON.parse('{"title":"找出使用 Systemd 的 Linux 系统关机缓慢或卡在关机界面的日志","description":"","frontmatter":{},"headers":[],"relativePath":"codes/search-systemd-shutdown-timeout.md","filePath":"codes/search-systemd-shutdown-timeout.md","lastUpdated":1768628240000}'),r={name:"codes/search-systemd-shutdown-timeout.md"};function s(c,e,n,i,l,p){return d(),o("div",null,[...e[0]||(e[0]=[t('<h1 id="找出使用-systemd-的-linux-系统关机缓慢或卡在关机界面的日志" tabindex="-1">找出使用 Systemd 的 Linux 系统关机缓慢或卡在关机界面的日志 <a class="header-anchor" href="#找出使用-systemd-的-linux-系统关机缓慢或卡在关机界面的日志" aria-label="Permalink to “找出使用 Systemd 的 Linux 系统关机缓慢或卡在关机界面的日志”">​</a></h1><p><code>sudo journalctl -b -1 --no-pager | egrep -i &quot;A stop job is running|timed out|timeout|stop|failed|dependency failed|killed process|shutdown&quot;</code></p><h2 id="解释" tabindex="-1">解释 <a class="header-anchor" href="#解释" aria-label="Permalink to “解释”">​</a></h2><p>用 <code>sudo</code> 提升权限以查看系统日志。</p><h3 id="journalctl" tabindex="-1"><code>journalctl</code> <a class="header-anchor" href="#journalctl" aria-label="Permalink to “journalctl”">​</a></h3><p><code>-b -1</code> 意思是查看上一次启动的日志。<code>-b -2</code> 是倒数第二次，以此类推。如果没有指定数字，则查看当前启动的日志。</p><p><code>--no-pager</code> 意思是不使用分页器，直接输出所有日志。</p><h3 id="egrep" tabindex="-1"><code>egrep</code> <a class="header-anchor" href="#egrep" aria-label="Permalink to “egrep”">​</a></h3><p><code>egrep</code> 是 <code>grep -E</code> 的简写，表示使用扩展正则表达式进行匹配。</p><p><code>-i</code> 表示忽略大小写。</p><p>引号里的内容是要匹配的关键词或短语，多个关键词用 <code>|</code> 分隔，表示“或”的关系。</p>',11)])])}const m=a(r,[["render",s]]);export{u as __pageData,m as default};
